Perfect! Now let me create the main workflow file. I'll create it using a file to avoid shell escaping issues:

```bash
cat > /tmp/issue-automation.yml << 'EOF'
name: Issue Management Automation

on:
  issues:
    types: [opened, labeled]

jobs:
  issue-triage:
    runs-on: ubuntu-latest
    if: github.event.action == 'opened'
    steps:
      - name: Auto-assign category and priority labels
        uses: actions/github-script@v7
        with:
          script: |
            const issue = context.payload.issue;
            const title = issue.title.toLowerCase();
            const body = (issue.body || '').toLowerCase();
            const labelsToAdd = [];

            // Add needs-triage label to all new issues
            labelsToAdd.push('needs-triage');

            // Category labels based on title
            if (title.includes('bug')) {
              labelsToAdd.push('bug');
            }
            if (title.includes('epic')) {
              labelsToAdd.push('epic');
            }
            if (title.includes('maintenance')) {
              labelsToAdd.push('maintenance');
            }

            // Priority labels based on title OR body (highest priority wins)
            const text = title + ' ' + body;
            let priority = 'priority-medium';

            if (text.includes('low') || text.includes('nice-to-have') || text.includes('minor')) {
              priority = 'priority-low';
            }
            if (text.includes('medium') || text.includes('normal')) {
              priority = 'priority-medium';
            }
            if (text.includes('important') || text.includes('high') || text.includes('blocking')) {
              priority = 'priority-high';
            }
            if (text.includes('critical') || text.includes('urgent') || text.includes('production') || text.includes('outage')) {
              priority = 'priority-critical';
            }

            labelsToAdd.push(priority);

            // Ensure all labels exist before adding
            const labelDefinitions = [
              { name: 'bug', description: 'Something isn\'t working', color: 'd73a4a' },
              { name: 'enhancement', description: 'New feature or request', color: 'a2eeef' },
              { name: 'epic', description: 'Large feature requiring multiple sub-tasks', color: '3e4b9e' },
              { name: 'maintenance', description: 'Maintenance and housekeeping tasks', color: 'fbca04' },
              { name: 'priority-critical', description: 'Critical priority issue', color: 'b60205' },
              { name: 'priority-high', description: 'High priority issue', color: 'd93f0b' },
              { name: 'priority-medium', description: 'Medium priority issue', color: 'fbca04' },
              { name: 'priority-low', description: 'Low priority issue', color: '0e8a16' },
              { name: 'needs-triage', description: 'Needs to be reviewed by maintainers', color: 'ededed' },
              { name: 'needs-review', description: 'Awaiting review from maintainers', color: 'd4c5f9' },
              { name: 'first-time-contributor', description: 'Issue created by first-time contributor', color: '7057ff' }
            ];

            for (const labelDef of labelDefinitions) {
              try {
                await github.rest.issues.getLabel({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  name: labelDef.name
                });
              } catch (error) {
                if (error.status === 404) {
                  await github.rest.issues.createLabel({
                    owner: context.repo.owner,
                    repo: context.repo.repo,
                    name: labelDef.name,
                    description: labelDef.description,
                    color: labelDef.color
                  });
                  console.log(`Created label: ${labelDef.name}`);
                }
              }
            }

            // Add all labels to the issue
            await github.rest.issues.addLabels({
              owner: context.repo.owner,
              repo: context.repo.repo,
              issue_number: issue.number,
              labels: labelsToAdd
            });

            console.log(`Added labels: ${labelsToAdd.join(', ')}`);

  task-breakdown:
    runs-on: ubuntu-latest
    if: github.event.action == 'opened' && contains(github.event.issue.title, 'Epic')
    steps:
      - name: Create sub-issues for Epic
        uses: actions/github-script@v7
        with:
          script: |
            const parentIssue = context.payload.issue;
            const parentTitle = parentIssue.title;
            const parentNumber = parentIssue.number;

            const tasks = [
              { name: 'Requirements Analysis', number: 1 },
              { name: 'Design and Architecture', number: 2 },
              { name: 'Implementation', number: 3 },
              { name: 'Testing and Documentation', number: 4 }
            ];

            const subIssueNumbers = [];

            // Create 4 sub-issues
            for (const task of tasks) {
              const subIssueTitle = `[SUBTASK] ${parentTitle} - Task ${task.number}: ${task.name}`;
              const subIssueBody = `Related to #${parentNumber}\n\nThis is a sub-task for the epic: ${parentTitle}`;

              const subIssue = await github.rest.issues.create({
                owner: context.repo.owner,
                repo: context.repo.repo,
                title: subIssueTitle,
                body: subIssueBody,
                labels: ['enhancement', 'needs-review']
              });

              subIssueNumbers.push(subIssue.data.number);
              console.log(`Created sub-issue #${subIssue.data.number}: ${subIssueTitle}`);
            }

            // Update parent issue with checklist
            const checklistItems = subIssueNumbers.map((num, idx) => 
              `- [ ] #${num} - Task ${idx + 1}: ${tasks[idx].name}`
            ).join('\n');

            const updatedBody = `${parentIssue.body || ''}\n\n## Epic Tasks\n${checklistItems}`;

            await github.rest.issues.update({
              owner: context.repo.owner,
              repo: context.repo.repo,
              issue_number: parentNumber,
              body: updatedBody
            });

            console.log(`Updated parent issue #${parentNumber} with task checklist`);

  auto-response:
    runs-on: ubuntu-latest
    if: github.event.action == 'opened'
    needs: [issue-triage]
    steps:
      - name: Check if first-time contributor and post response
        uses: actions/github-script@v7
        with:
          script: |
            const issue = context.payload.issue;
            const issueAuthor = issue.user.login;

            // Check if this is the author's first issue in this repository
            const issues = await github.rest.issues.listForRepo({
              owner: context.repo.owner,
              repo: context.repo.repo,
              creator: issueAuthor,
              state: 'all',
              per_page: 100
            });

            const isFirstIssue = issues.data.length === 1;

            if (isFirstIssue) {
              // Add first-time-contributor label
              await github.rest.issues.addLabels({
                owner: context.repo.owner,
                repo: context.repo.repo,
                issue_number: issue.number,
                labels: ['first-time-contributor']
              });

              // Post welcome message
              await github.rest.issues.createComment({
                owner: context.repo.owner,
                repo: context.repo.repo,
                issue_number: issue.number,
                body: `ðŸ‘‹ Welcome @${issueAuthor}! Thank you for creating your first issue in this repository. We appreciate your contribution and will review it soon.`
              });

              console.log(`Added first-time-contributor label and welcome message for @${issueAuthor}`);
            }

            // Get current labels
            const currentIssue = await github.rest.issues.get({
              owner: context.repo.owner,
              repo: context.repo.repo,
              issue_number: issue.number
            });

            const labels = currentIssue.data.labels.map(l => l.name);

            // Post response based on issue type
            let responseMessage = '';

            if (labels.includes('bug')) {
              responseMessage = `## Bug Report Guidelines\n\nThank you for reporting this bug! To help us resolve it quickly, please ensure your report includes:\n\n- **Clear reproduction steps** - Help us reproduce the issue\n- **Expected vs actual behavior** - What should happen vs what happens\n- **Environment details** - OS, Node.js version, browser, etc.\n- **Screenshots or logs** - Visual evidence helps!\n\nOur team will investigate this issue and update you soon.`;
            } else if (labels.includes('epic')) {
              responseMessage = `## Feature Request Process\n\nThank you for this epic feature request! We've broken this down into manageable sub-tasks.\n\n**Next Steps:**\n1. Review the sub-tasks created for this epic\n2. Each sub-task will be tracked separately\n3. We'll update the checklist as we progress\n4. Feel free to contribute to any of the sub-tasks!\n\nWe're excited about this feature and will keep you updated on our progress.`;
            } else if (labels.includes('maintenance')) {
              responseMessage = `## Maintenance Guidelines\n\nThank you for identifying this maintenance task! \n\n**Review Process:**\n- We'll assess the impact and scope\n- Priority will be set based on technical debt severity\n- Implementation will be scheduled accordingly\n\n**Contributing:**\nIf you'd like to contribute to this maintenance task, please:\n1. Comment on this issue expressing your interest\n2. Wait for approval from maintainers\n3. Follow our coding standards and guidelines\n\nThank you for helping keep our codebase healthy!`;
            }

            if (responseMessage) {
              await github.rest.issues.createComment({
                owner: context.repo.owner,
                repo: context.repo.repo,
                issue_number: issue.number,
                body: responseMessage
              });
              console.log(`Posted auto-response for issue type`);
            }

            // Set milestone for high and critical priority issues
            if (labels.includes('priority-high') || labels.includes('priority-critical')) {
              // Get or create v1.0.0 milestone
              let milestone = null;
              try {
                const milestones = await github.rest.issues.listMilestones({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  state: 'open'
                });
                milestone = milestones.data.find(m => m.title === 'v1.0.0');

                if (!milestone) {
                  const created = await github.rest.issues.createMilestone({
                    owner: context.repo.owner,
                    repo: context.repo.repo,
                    title: 'v1.0.0',
                    description: 'Release version 1.0.0'
                  });
                  milestone = created.data;
                }
              } catch (error) {
                console.log(`Error handling milestone: ${error.message}`);
              }

              if (milestone) {
                await github.rest.issues.update({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  issue_number: issue.number,
                  milestone: milestone.number
                });
                console.log(`Set milestone v1.0.0 for high/critical priority issue`);
              }
            }

            // Change status from needs-triage to needs-review
            const labelsToAdd = [];
            const labelsToRemove = [];

            if (labels.includes('needs-triage')) {
              labelsToRemove.push('needs-triage');
              labelsToAdd.push('needs-review');
            }

            if (labelsToRemove.length > 0) {
              for (const label of labelsToRemove) {
                await github.rest.issues.removeLabel({
                  owner: context.repo.owner,
                  repo: context.repo.repo,
                  issue_number: issue.number,
                  name: label
                });
              }
            }

            if (labelsToAdd.length > 0) {
              await github.rest.issues.addLabels({
                owner: context.repo.owner,
                repo: context.repo.repo,
                issue_number: issue.number,
                labels: labelsToAdd
              });
            }

            console.log(`Changed status from needs-triage to needs-review`);
